#!/bin/usr/env python3

seq = ''

nt_count = {}
unique = set(seq)
total = 0
cg = 0

for nt in unique:
	count = seq.count(nt)
	nt_count[nt] = count
	
for nt in seq:
	total += 1
	if nt == 'G' or nt == 'C':
		cg += 1

print('total nt content: ', total)
print('nt count: ', nt_count)
print('cg count: ', cg)
cg_content = (cg / total) * 100
print('cg content: ', f'{cg_content:.2f}')

